let inputs=document.getElementsByTagName('input');let index=0;let timerId=null;function switchImg(){index++;if(index>=inputs.length){index=0}inputs[index].checked=true}timerId=setInterval(switchImg,3000);document.getElementById('main').onmouseenter=function(){clearInterval(timerId)};document.getElementById('main').onmouseleave=function(){timerId=setInterval(switchImg,3000)}